import Players from "./Players";
import {Button,Form, FormControl, FormGroup} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Fragment, useState } from "react";
import { Link,useNavigate } from "react-router-dom";
import {v4 as uuid} from "uuid";

const Add=()=>{
    let history=useNavigate();
    const[details,setDetails]=useState({
        name:"",
        age:""
    })

    const handleChange=(e)=>{
        const {name,value}=e.target;
        setDetails((prev)=>{
            return {...prev,[name]:value}
        })
    }
    console.log(details);

    const handleSubmit=(e)=>{
        e.preventDefault();
        const ids=uuid();
        let uniqueId=ids.slice(0,8);
    
        Players.push({Id:uniqueId,Name:details.name,Age:details.age})
        history('/');
    }

    return(<Fragment>
        <div className="container-fluid p-5 bg-primary text-white text-center">
        <h1>ADD Operation</h1>
        </div>
        <Form onSubmit={handleSubmit} className="d-grid gap-2" style={{margin:"8rem"}}>
            <FormGroup className="mb-3" controlId="formName">
                <FormControl className="form-control-lg" type="text" name="name"  placeholder="Enter Name" required onChange={handleChange}/>
                <Form.Control.Feedback type="invalid">
                  Please enter your name.
                </Form.Control.Feedback>
            </FormGroup>
            <FormGroup className="mb-3" controlId="formAge">
                <FormControl className="form-control-lg" type="number" name="age" placeholder="Enter Age" required onChange={handleChange}/>
                <Form.Control.Feedback type="invalid">
                    Please enter your age.
                </Form.Control.Feedback>
            </FormGroup>
            <Button className="form-control-lg" type="submit" >Add Player</Button>
        </Form>
    </Fragment>)
}

export default Add;
