import Players from "./Players";
import {Button,Form, FormControl, FormGroup} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Fragment, useState,useEffect } from "react";
import { Link,useNavigate } from "react-router-dom";


const Edit=()=>{
    let history=useNavigate();

    const[details,setDetails]=useState({
        name:"",
        age:""
    })
    const[id,setId]=useState("")
    var index=Players.map((e)=>{
        return e.id;
    }).indexOf(id);

    const handleChange=(e)=>{
        const{name,value}=e.target;
        setDetails((prev)=>{
            return {...prev,[name]:value}
        })
    }
    
    const handleSubmit=(e)=>{
        e.preventDefault();
        
        const a=Players[index];
        a.name=details.name;
        a.age=details.age;


        history('/');
    }

    return(<Fragment>
        <div className="container-fluid p-5 bg-primary text-white text-center">
        <h1>Edit Operation</h1>
        </div>
        <Form onSubmit={handleSubmit} className="d-grid gap-2" style={{margin:"8rem"}}>
            <FormGroup className="mb-3" controlId="formName">
                <FormControl className="form-control-lg" type="text" value={details.name} name="name"  placeholder="Enter Name" required onChange={handleChange}/>
                <Form.Control.Feedback type="invalid">
                  Please enter your name.
                </Form.Control.Feedback>
            </FormGroup>
            <FormGroup className="mb-3" controlId="formAge">
                <FormControl className="form-control-lg" type="number" value={details.age} name="age" placeholder="Enter Age" required onChange={handleChange}/>
                <Form.Control.Feedback type="invalid">
                    Please enter your age.
                </Form.Control.Feedback>
            </FormGroup>
            <Button className="form-control-lg" type="submit" >Update Player</Button>
        </Form>
    </Fragment>)

}

export default Edit;
