import Players from "./Players";
import {Button,Form, FormControl, FormGroup} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Fragment, useState,useEffect } from "react";
import { Link,useNavigate } from "react-router-dom";


const Edit=()=>{
    let history=useNavigate();

    const[name,setName]=useState("");
    const[age,setAge]=useState("");
    const[id,setId]=useState("")

    var index=Players.map((e)=>{
        return e.Id;
    }).indexOf(id);


    useEffect(()=>{
       setName(localStorage.getItem('name'))
       setAge(localStorage.getItem('age'))
       setId(localStorage.getItem('id'))
    },[])

    
    const handleSubmit=(e)=>{
        e.preventDefault();
        
        let a =Players[index];
        a.Name=name;
        a.Age=age;


        history('/');
    }

    return(<Fragment>
        <div className="container-fluid p-5 bg-primary text-white text-center">
        <h1>Edit Operation</h1>
        </div>
        <Form  className="d-grid gap-2" style={{margin:"8rem"}}>
            <FormGroup className="mb-3" controlId="formName">
                <FormControl className="form-control-lg" type="text" value={name} 
                placeholder="Enter Name" 
                required 
                onChange={(e)=>setName(e.target.value)}/>
                <Form.Control.Feedback type="invalid">
                  Please enter your name.
                </Form.Control.Feedback>
            </FormGroup>
            <FormGroup className="mb-3" controlId="formAge">
                <FormControl className="form-control-lg" type="number" value={age}
                placeholder="Enter Age" 
                required 
                onChange={(e)=>setAge(e.target.value)}/>
                <Form.Control.Feedback type="invalid">
                    Please enter your age.
                </Form.Control.Feedback>
            </FormGroup>
            <Button className="form-control-lg" type="submit" onClick={(e)=>handleSubmit(e)}>Update Player</Button>
        </Form>
    </Fragment>)

}

export default Edit;
