import Players from "./Players";
import {Button,Form, FormControl, FormGroup} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Fragment, useState } from "react";
import { Link,useNavigate } from "react-router-dom";
import {v4 as uuid} from "uuid";

const Add=()=>{
    let history=useNavigate();
    const[details,setDetails]=useState({
        name:"",
        age:""
    })

    const handleChange=(e)=>{
        const {name,value}=e.target;
        setDetails((prev)=>{
            return {...prev,[name]:value}
        })
    }
    console.log(details);

    // const handleSubmit=(e)=>{
    //     e.preventDefault();
    //     const ids=uuid();
    //     let uniqueId=ids.slice(0,8);

    //     Players.push({id:uniqueId,Name:details.name,Age:details.age})
    //     history('/');
    // }

    return(<Fragment>
        <Form className="d-grid gap-2" style={{margin:"15rem"}}>
            <FormGroup className="mb-3" controlId="formName">
                <FormControl type="text" placeholder="Enter Name" required onChange={handleChange}/>
            </FormGroup>
            <FormGroup className="mb-3" controlId="formAge">
                <FormControl type="number" placeholder="Enter Age" required onChange={handleChange}/>
            </FormGroup>
            <Button>Add Player</Button>
        </Form>
    </Fragment>)
}

export default Add;
