import Players from "./Players";
import {Table,Button} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Fragment } from "react";

const Home=()=>{

    return(<Fragment>
        <div style={{margin:"10rem"}}>
            <Table stripped bordered hover size="md">
                <thead>
                    <tr>
                        <td>S.No.</td>
                        <td>Name</td>
                        <td>Age</td>
                        <td>Operations</td>
                    </tr>
                </thead>
                <tbody>
                    {
                        Players && Players.length>0
                        ?
                        Players.map((person)=>{
                            return(
                                <tr>
                                    <td>{person.id}</td>
                                    <td>{person.name}</td>
                                    <td>{person.Age}</td>
                                    <td>
                                        <Button>Edit</Button>
                                        &nbps
                                        <Button>Delete</Button>
                                    </td>
                                </tr>
                            )
                        })
                        :
                        "No Record available!"
                    }
                </tbody>
            </Table>
        </div>
    </Fragment>)
}

export default Home;
