import Players from "./Players";
import {Button,Form} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Fragment, useState,useEffect, useMemo,useCallback } from "react";
import { Link,useNavigate } from "react-router-dom";


const Edit=()=>{
    const[name,setName]=useState("");
    const[age,setAge]=useState("");
    const[id,setId]=useState("");

    let history=useNavigate();

    const index = useMemo(() => Players.map((e) => e.id).indexOf(id), [id]);
     
   
    

    // const handleSubmit=(e)=>{
    //     e.preventDefault();
        
    //     let a =Players[index];
    //     a.Name = name; 
    //     a.Age = age;    
    //     history('/');
    // }
    const handleSubmit = useCallback((e) => {
        e.preventDefault();
     
        // you can use const as objects are mutable
        const obj = Players[index] || {}; // this will make sure there will be an object
     
        obj.Name = name;
        obj.Age = age;
     
        history('/');
     }, [index]);

    useEffect(()=>{
       setName(localStorage.getItem('Name'))
       setAge(localStorage.getItem('Age'))
       setId(localStorage.getItem('Id'))
    },[])

    return(<Fragment>
        <div className="container-fluid p-5 bg-primary text-white text-center">
        <h1>Edit Operation</h1>
        </div>
        <Form className="d-grid gap-2" style={{margin:"8rem"}} onSubmit={handleSubmit}>
            <Form.Group className="mb-3" controlId="formName">
                <Form.Control className="form-control-lg" type="text" value={name} 
                placeholder="Enter Name" 
                required 
                onChange={(e)=>setName(e.target.value)}/>
            </Form.Group>
            <Form.Group className="mb-3" controlId="formAge">
                <Form.Control className="form-control-lg" type="number" value={age}
                placeholder="Enter Age" 
                required 
                onChange={(e)=>setAge(e.target.value)}/>
            </Form.Group>
            <Button 
            className="form-control-lg" type="submit" >Update Player</Button>
        </Form>
    </Fragment>)

}

export default Edit;
